package org.controlstatement;

public class nested_if {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int age=38;
if(age>30) 
	{
		if(age>80)
		{ 
			System.out.println("my age is greater than 80");
		}else
		{
			System.out.println("my age is greater than 30");
		}
	}
	else {
	System.out.println("not my age");
}
	}	
}


